INSERT INTO `posts` (`id`, `title`, `slug`, `body`, `image`, `cat_id`, `user_id`, `published_at`, `status`, `created_at`, `updated_at`, `deleted_at`) VALUES
(2, 'قیمت مسکن در تهران ماهانه ۳ درصد رشد کرد', 'قیمت-مسکن-در-تهران-ماهانه-۳-درصد-رشد-کرد', '<h2>قیمت مسکن در تهران ماهانه ۳ درصد رشد کرد</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>در ۱۲ ماهه منتهی به دی ماه ۱۳۹۸ میانگین قیمت مسکن در شهر تهران، ماهانه ۳.۱ درصد افزایش پیدا کرده است. به گزارش تجارت&zwnj;نیوز، تحولات بازار مسکن در شهر تهران از بهمن ماه ۱۳۹۷ تا دی ماه ۱۳۹۸ نشان می&zwnj;دهد، میانگین رشد متوسط قیمت مسکن به صورت ماه به ماه قبل برای یک دوره ۱۲ ماهه منتهی به دی ماه سال جاری در شهر تهران ۳.۱ درصد رشد بوده است. در این بازه زمانی متوسط قیمت مسکن در اردیبهشت ماه ۱۳۹۸ بیشترین رشد معادل ۱۱.۴ درصد و در شهریور ماه کمترین رشد معادل ۳.۵- را تجربه کرده است. این آمار که از سوی دفتر اقتصاد مسکن وزارت راه و شهرسازی ارائه شده همچنین گویای آن است که میانگین نرخ رشد ۱۲ ماهه قیمت مسکن شهر تهران منتهی به دی ماه ۱۳۹۸ حدود ۵.۵ درصد بوده است. همچنین ارزش ریالی معاملات انجام&zwnj;شده در دی ماه ۱۱ هزار و ۹۲۰ میلیارد تومان بوده که نسبت به ماه مشابه سال قبل ۱۱۱ درصد رشد نشان می دهد.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>از میان مناطق ۲۲ گانه شهر تهران منطقه ۱ با ۳۰ میلیون و ۲۲ هزار تومان در هر متر مربع بیشترین و منطقه ۱۸ با ۶ میلیون و ۴۹۰ هزار تومان کمترین متوسط قیمت را داشته&zwnj;اند. این گزارش همچنین حاکی است که مناطق ۱، ۳، ۲، ۶، ۵ و ۴ به ترتیب مناطقی هستند که متوسط قیمت در آنها از متوسط شهر تهران بیشتر بوده است. بیشترین رشد متوسط قیمت در دی ماه ۱۳۹۸ نسبت به ماه مشابه سال قبل به منطقه ۱۶ به میزان ۶۸ درصد تعلق داشته و کمترین میزان رشد نیز در منطقه ۱ به میزان ۳۱ درصد رخ داده است. از سوی دیگر بررسی متوسط قیمت مسکن بر اساس متراژهای مختلف واحدهای مسکونی نشان می&zwnj;دهد، قیمت در دی ماه سال جاری در کلیه بازه&zwnj;ها دارای رشد بوده و واحدهای مسکونی با متراژ کمتر از ۴۰ متر مربع بیشترین رشد قیمت را به میزان ۵۱.۶ درصد و واحدهای با متراژ ۱۶۰ تا ۱۸۰ متر مربع کمترین رشد قیمت را به میزان ۲۶.۲ درصد نسبت به ماه مشابه سال قبل تجربه کرده&zwnj;اند. لازم به ذکر است در دی ماه ۱۳۹۸ تعداد کل معاملات مسکن شهر تهران به ۱۰.۹ هزار واحد رسید که نسبت به ماه قبل ۱۲.۴ درصد نسبت به ماه مشابه سال قبل ۵۸.۲ درصد رشد داشته است